sql.if:
left join "street_attribute" da_c on da_c.domain_id = d.id and da_c.entity_attribute_id = 1 and da_c.status = 1 left join "city" city on city.object_id = da_c.number and city.status = 1 left join "city_attribute" city_a_r on city_a_r.domain_id = city.id and city_a_r.entity_attribute_id = 1 and city_a_r.status = 1 left join "region" r on r.object_id = city_a_r.number and r.status = 1
left join "region_attribute" ra_n on ra_n.domain_id = r.id and ra_n.entity_attribute_id = 2 and ra_n.status = 1 left join "region_value" rv_n on rv_n.attribute_id = ra_n.id and rv_n.locale_id = #{localeId}
left join "region_attribute" ra_c on ra_c.domain_id = r.id and ra_c.entity_attribute_id = 1 and ra_c.status = 1 left join "country" c on c.object_id = ra_c.number and c.status = 1 left join "country_attribute" ca_n on ca_n.domain_id = c.id and ca_n.entity_attribute_id = 1 and ca_n.status = 1 left join "country_value" cv_n on cv_n.attribute_id = ca_n.id and cv_n.locale_id = #{localeId}

